CREATE TABLE chessboard-? (idchessboard int(11) NOT NULL AUTO_INCREMENT, cellChar varchar(1) NOT NULL, cellInt int(11) NOT NULL, standing varchar(100) DEFAULT NULL, price int(11) DEFAULT NULL, TimeSets datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, partyID longtext DEFAULT ?, PRIMARY KEY (idchessboard), UNIQUE KEY idx_chessboard_cellChar_cellInt (cellChar,cellInt)) ENGINE=InnoDB AUTO_INCREMENT=65 DEFAULT CHARSET=utf8;
